'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
    fs = require('graceful-fs');

module.exports = {
	directories: function() {
	
	var done = this.async();
		
	this.cssContext = {
      cssBase: this.cssBase,
  }
	
	// Save to yo-config.json
  this.config.set('css', this.cssContext);
  this.config.save();
  done();
}}


