'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
		printTitle  = require('./../../helpers/printTitle'),
    fs = require('graceful-fs');

module.exports = {
	cssBase: function() {
		
		this.log(printTitle('Database Configuration'))
		
		var done = this.async();
		this.prompt([{
      // Wordpress Database Info
      name: 'dbName',
      message: 'Database name',
      default: 'database_name'
    },{
      name: 'dbUser',
      message: 'Database user',
      default: 'username'
    }, {
      name: 'dbPass',
      message: 'Database password',
      default: ''
    }], 

		function(props){
			this.props = props;
			// To access props later use this.props.someAnswer;
	     // Database info
      this.dbName = this.props.dbName;
      this.dbUser = this.props.dbUser;
      this.dbPass = this.props.dbPass;
			done();
		}.bind(this))
	}};