'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
    fs = require('graceful-fs');

module.exports = {
	directories: function() {
	
	var done = this.async();
		
	this.directoriesContext = {
      srcDir: this.srcDir,
     	buildDir: this.buildDir
  }
	
	// Save to yo-config.json
  this.config.set('directories', this.directoriesContext);
  this.config.save();
  done();
}}


