'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
		printTitle  = require('./../../helpers/printTitle'),
    fs = require('graceful-fs');

module.exports = {
	directories: function() {
		
		this.log(printTitle('Directory Structure'))
		
		var done = this.async();
		this.prompt([ {
      // Source directory
      name: "srcDir",
      message: "Set source directory",
      default: "src"
    }, {
      // Build dir
      name: "buildDir",
      message: "Set build directory",
      default: "dist"
    }], 

		function(props){
			this.props = props;
			// To access props later use this.props.someAnswer;
			 this.srcDir = this.props.srcDir,
	     this.buildDir = this.props.buildDir
			done();
		}.bind(this))
	}};