'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
    fs = require('graceful-fs');

module.exports = {
	gulp: function() {
		var done = this.async();
	
		var gulpContext = {
			srcDir: this.srcDir,
			buildDir: this.buildDir,
			wpDir: this.wpDir,
			wpContentDir: this.wpContentDir,
			themeDirName: this.themeDirName,
			projectNameJson: this.projectNameJson,
			projectVersion: this.projectVersion,
			projectDescription: this.projectDescription,
			projectAuthor: this.projectAuthor,
			
			siteUrl: this.wpUrl,
			siteTitle: this.wpTitle,
			admin: this.wpAdmin,
			adminPass: this.wpAdminPass,
			adminEmail: this.wpAdminEmail

		}
	
		this.fs.copyTpl(
      this.templatePath('gulp'),
      this.destinationPath(), gulpContext
    ),
		done();
	
}}