'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
    fs = require('graceful-fs');

module.exports = {
	webfiles: function() {
		var done = this.async();
		
		// Copy non dotfiles
		this.fs.copy(
      this.templatePath('website/**/*'),
      this.destinationPath(this.srcDir)
    );
		
		this.fs.copy(
      this.templatePath('website/**/.*'),
      this.destinationPath(this.srcDir)
    ),
		done();
	
}}