'use strict';

var yeoman = require('yeoman-generator'),
    chalk = require('chalk'),
    yosay = require('yosay'),
    fs = require('graceful-fs');

module.exports = {
	wordpress: function() {
	
	var done = this.async();
		
	this.wordpressContext = {
		// Wordpress dirs
		wpDir: this.wpDir,
		wpContentDir: this.wpContentDir,
		themeDirName: this.themeDirName
	}
	
	// Save to yo-config.json
  this.config.set('wordpress-info', this.wordpressContext);
  this.config.save();
  done();
}}


