var gulp = require('gulp'),
		del = require('del'),
		wp_themes_dir = '<%= buildDir %>/<%= wpContentDir %>/themes',
		theme_dir = '<%= themeDirName %>';

gulp.task('clean', function(cb) {
	del( wp_themes_dir + '/' + theme_dir )
		del(['<%= buildDir %>/.htaccess', '<%= buildDir %>/robots.txt'])
});

gulp.task('build', [
	'clean',
	'styles', 
	'minify',
	'scripts',
	'images',
	'move-files'
]);