var gulp = require('gulp'),
		jade = require('gulp-jade-php'),
		wp_themes_dir = '<%= buildDir %>/<%= wpContentDir %>/themes',
		theme_dir = '<%= themeDirName %>';

gulp.task('minify', function() {
		gulp.src('<%= srcDir %>/**/*.jade')
			.pipe(jade())
			.pipe(gulp.dest( wp_themes_dir + '/' + theme_dir));
		
		gulp.src('<%= srcDir %>/*.php')
			.pipe(gulp.dest(  wp_themes_dir + '/' + theme_dir ));
});