var gulp = require('gulp'),
		cwebp = require('gulp-cwebp');
		
    gulp.task('wp-cwebp', function () {
      gulp.src('<%= buildDir %>/<%= wpContentDir %>/uploads/**/**/*.{jpg,png}')
        .pipe(cwebp())
        .pipe(gulp.dest('<%= buildDir %>/<%= wpContentDir %>/uploads'));
    });

    gulp.task('theme-cwebp', function () {
      gulp.src('<%= srcDir %>/img/**/*.{jpg,png}')
        .pipe(cwebp())
        .pipe(gulp.dest('<%= srcDir %>/img'));
    });