var gulp = require('gulp'),
	shell = require('gulp-shell'),
	buildDir = '<%= buildDir %>/<%= wpDir %>',
	url = '<%= siteUrl %>',
	siteTitle = '<%= siteTitle %>',
	admin = '<%= admin %>',
	adminPass = '<%= adminPass %>',
	adminEmail = '<%= adminEmail %>';

var tasks = [
    'cd '+ buildDir,
    'wp core install --skip-email --url='+url+' --title='+siteTitle+' --admin_user='+admin+' --admin_password='+adminPass+' --admin_email='+adminEmail+' --path='+buildDir+''
]

gulp.task('installWP', shell.task( tasks ) )

