var sshUser = 'username';

var gulp = require('gulp'),
	shell = require('gulp-shell'),
    
    options = '-avz'
    
    devDest = '/path/to/staging/wp-content/themes/'
    prodDest = '/path/to/production/wp-content/themes/'
    
    localDir = '<%= buildDir %>/<%= wpContentDir %>/themes/<%= themeDirName %>'
    

gulp.task('push-theme-dev', shell.task([
    'rsync ' + localDir + ' ' + sshUser + ':'+ devDest + ' ' + options
]))

gulp.task('push-theme-prod', shell.task([
    'rsync ' + localDir + ' ' + sshUser + ':'+ prodDest + ' ' + options
]))

