var sshUser = 'username';

var gulp = require('gulp'),
	shell = require('gulp-shell'),
    options = '-avz'
    exclude = '--exclude="wp-migrate-db"'
    
    devSrc = '/path/to/staging/wp-content/uploads/20*'
    prodSrc = '/path/to/production/wp-content/uploads/20*'
    
    devDest = '/path/to/staging/wp-content/uploads/'
    prodDest = '/path/to/prod/wp-content/uploads/'
    
    localDir = '<%= buildDir %>/<%= wpContentDir %>/uploads/'
    

gulp.task('pull-uploads', shell.task([
    'rsync' + sshUser + ':'+ prodSrc + ' ' + localDir + ' ' + options
]))

gulp.task('push-uploads-dev', shell.task([
    'rsync ' + localDir+'20*' + ' ' + sshUser + ':'+devDest + ' ' + options
]))

gulp.task('push-uploads-prod', shell.task([
    'rsync ' + localDir+'20*' + ' ' + sshUser + ':' +prodDest + ' ' + options
]))