var gulp = require('gulp'),
	shell = require('gulp-shell');


gulp.task('update-wp', [
    'update-core',
    'update-plugins',
    'update-themes'
])
gulp.task('update-core', shell.task([
    'wp core update',
]))

gulp.task('update-plugins', shell.task([
    'wp plugin update --all',
    'rm -Rf src/plugins',
    'cp dist/wp-content/plugins src -Rf',
]))

gulp.task('update-themes', shell.task([
    'wp theme update --all',
]))