var gulp = require('gulp'),
		watch = require('gulp-watch');

gulp.task('watch', function() {   
    // Watch .css files
    gulp.watch('<%= srcDir %>/css/**/*', ['styles']);
    //Watch .js files
    gulp.watch('<%= srcDir %>/js/**/*.js', ['scripts']); 
    // Watch image files
    gulp.watch('<%= srcDir %>/img/**/*', ['images']);
    // Watch template files
    gulp.watch('<%= srcDir %>/**/*.{php,jade}', ['minify']);
    // Watch function partials
    gulp.watch('<%= srcDir %>/functions/*.php', ['move-function-partials']);
});