<?php

// Custom Scripts		
	function scriptsAndStyles() {
		// Register Scripts
			wp_register_script( 'initScripts', get_template_directory_uri() . '/js/scripts.min.js', null, null, true );
			wp_register_script( 'fontAwesome', 'https://use.fontawesome.com/654c0ca81b.js', null, null, true );
			wp_register_script( 'addThis', '//s7.addthis.com/js/300/addthis_widget.js#pubid=ra-5887d27b8378735f', null, null, true );
			
			wp_register_script( 'globalScripts', get_template_directory_uri() . '/js/globalScripts.min.js', null, null, true );
		// Enqueue Scripts		
			wp_enqueue_script('jquery');
			wp_enqueue_script( 'globalScripts' );
			wp_enqueue_script( 'fontAwesome' );
			wp_enqueue_script( 'addThis' );
			

		// Enqueue Styles
			wp_enqueue_style( 'googleFonts', 'https://fonts.googleapis.com/css?family=Bitter|Raleway:100,300,400');
			wp_enqueue_style( 'customStyle', get_template_directory_uri() . '/css/global.min.css');
	};
	add_action( 'wp_enqueue_scripts', 'scriptsAndStyles' );
?>