// Get current URL
var getUrl  = window.location;
// Get the base URL
var baseUrl = getUrl .protocol + "//" + getUrl.host + "/" + getUrl.pathname.split('/')[1];

// API base
var base      = baseUrl+"/wp-json";
//  API Namespace
var namespace = "/wp/v2";
// Path to the API
var path      = base+namespace;

//  Start routes

// Route to page data
var pages     = path+"/pages/";

// Route to posts data
var posts     = path+"/posts/";

// Route to user data
var users     = path+"/users/";

// Route to ACF data
var acf       = path+"/acf/"; 

routes =  {
    pages: pages,
    posts: posts,
    acf: acf,
    users: users
}



