'use strict';

var assert = require('assert');
var mapReplace = require('./index');

describe('mapReplace', function() {
	it('should replace whole string', function() {
		assert.equal(mapReplace('-[]/{}()*+?.^$|', {
			'-[]/{}()*+?.^$|': 'clean as a whistle'
		}), 'clean as a whistle');
	});

	it('should replace part of string', function() {
		assert.equal(mapReplace('hello world', {
			'hello': 'hello again'
		}), 'hello again world');
	});

	it('should replace multiple times', function() {
		assert.equal(mapReplace('hello world', {
			'hello': 'hello again',
			'world': 'internet'
		}), 'hello again internet');
	});

	it('should not replace anything', function() {
		assert.equal(mapReplace('hello world', {
			'how are you': 'good'
		}), 'hello world');
	});
});
