import React, { Component } from 'react'
import Menu, { Item, Header } from '../src/collections/menu'
import Container from '../src/elements/container'

export default class App extends Component {
    render() {
        return (
            <div>
                <Menu vertical inverted className="top fixed">
                    <Item>
                        <Header>Elements</Header>
                        <Menu>
                            <Item href="/button">Button</Item>
                            <Item href="/container">Container</Item>
                            <Item href="/divider">Divider</Item>
                            <Item href="/flag">Flag</Item>
                            <Item href="/header">Header</Item>
                            <Item href="/icon">Icon</Item>
                            <Item href="/image">Image</Item>
                            <Item href="/input">Input</Item>
                            <Item href="/label">Label</Item>
                            <Item href="/list">List</Item>
                            <Item href="/loader">Loader</Item>
                            <Item href="/rail">Rail</Item>
                            <Item href="/reveal">Reveal</Item>
                            <Item href="/segment">Segment</Item>
                            <Item href="/step">Step</Item>
                        </Menu>
                    </Item>
                    <Item>
                        <Header>Collections</Header>
                        <Menu>
                            <Item href="/menu">Menu</Item>
                        </Menu>
                    </Item>
                </Menu>
                <Container>
                    {this.props.children}
                </Container>
            </div>
        )
    }
}