import React, { PropTypes } from 'react'
import classNames from 'classnames'

const Label = ({children, basic, size, color, tag, circular, className}) => {
    className = classNames('ui', {
        basic: basic,
        tag: tag,
        circular: circular,
    }, size, color, className, 'label')

    return (
        <div className={className}>
            {children}
        </div>
    )
}

Label.propTypes = {
    basic: PropTypes.bool,
    children: PropTypes.node,
    tag: PropTypes.bool,
    circular: PropTypes.bool,
    color: PropTypes.oneOf(['red', 'orange', 'yellow', 'olive', 'green', 'teal', 'blue', 'violet', 'purple', 'pink', 'brown', 'grey', 'black']),
    size: PropTypes.oneOf(['mini', 'tiny', 'small', 'large', 'big', 'huge', 'massive']),
    className: PropTypes.string,
}

export default Label