import { Invoices } from './endpoints/invoices';
import { Contacts } from './endpoints/contacts';
import { Organisation } from './endpoints/organisation';
import { Receipts } from './endpoints/receipts';
import { Items } from './endpoints/items';

export class Xero {
  public contacts: Contacts;
  public invoices: Invoices;
  public organisation: Organisation;
  public receipts: Receipts;
  public items: Items;
  constructor (config) {
    this.contacts = new Contacts(config);
    this.invoices = new Invoices(config);
    this.organisation = new Organisation(config);
    this.receipts = new Receipts(config);
    this.items = new Items(config);
  }
}