import { Methods, IOptions } from './paths';

export class XeroProto extends Methods {
  constructor (config, public endpoint: string) {
    super(config, endpoint);
  }
  findById (id, opts: IOptions = {}) {
    opts.singular = true;
   return  super.get(`/${this.endpoint}/${id}`, opts);
  }
  findAll (opts: IOptions = {}) {
    return super.get(`/${this.endpoint}`, opts);
  }
  create (body, opts: IOptions = {}) {
    opts.singular = true;
    return super.put(`/${this.endpoint}`, body);
  }
  bulkCreate (body, opts: IOptions = {}) {
    return super.put(`/${this.endpoint}?summarizeErrors=false`,  body, opts);
  }
  createOrUpdate (body, opts: IOptions = {}) {
    opts.singular = true;
    return super.post(`/${this.endpoint}`, body, opts);
  }
  bulkCreateOrUpdate (body, opts: IOptions = {}) {
    return super.post(`/${this.endpoint}`, body, opts);
  }
}
