'use strict';
var should = require('should');
var Xero = require('../').Xero;


var config = require('../config/config.json')['test'];
var fs = require('fs');

	var config = {	key:config.ConsumerKey, 
					secret:config.ConsumerSecret, 
					rsa_key: fs.readFileSync(config.Path, "utf-8")
				};





var xero = new Xero(config);

describe('Receipts', function(){
	this.timeout(10000);
	describe('findAll', function(){
		it('Should Find All Receipts', function(done){
			xero.receipts.findAll().then(function(json){
	 			should.equal(json.length > 0, true);
				done();
			}).catch(done);
		});
	});
});