import { Mixin } from 'mixwith';
import Range from './Range';

const RangeableObject = Mixin((superclass) => class extends superclass {
  constructor(...args) {
    super(...args);
    this.__meta__ = Object.assign({}, this.__meta__, {
      rangeDefinition: new Range(0, Infinity)
    });
  }

  get rangeDefinition() {
    return this.__meta__.rangeDefinition;
  }

  set rangeDefinition(_) {
    throw new Error('RangeableObject#rangeDefinition: object cannot be replaced');
  }
});

export default RangeableObject;
